choose.when:
SELECT B.* FROM (
SELECT ROWNUM as r, A.* FROM (
SELECT book_donate.* FROM
book_donate where id like '%'||#{search}||'%' ORDER BY no
DESC)A
)B WHERE B.r BETWEEN #{begin} AND #{end}

choose.otherwise:
SELECT B.* FROM (
SELECT ROWNUM as r, A.* FROM (
SELECT book_donate.* FROM
book_donate ORDER BY no DESC)A
)B WHERE B.r BETWEEN #{begin} AND #{end}
